mapper.update:
UPDATE file
   SET file_name = #{fileName}
      ,upd_date = now()
WHERE no = #{no}

mapper.delete:
DELETE FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}

mapper.select:
SELECT *
FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
ORDER BY reg_date DESC
